import React, { useState } from 'react';
import styled from 'styled-components';

const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  max-width: 400px;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-bottom: 20px;
`;

const Description = styled.p`
  color: #666;
  margin-bottom: 20px;
  text-align: center;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 16px;
`;

const ErrorMessage = styled.p`
  color: #ff0000;
  font-size: 14px;
  margin: 5px 0;
`;

const LoginButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #e7f0ff;
  border: none;
  border-radius: 4px;
  color: #000;
  font-size: 16px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #d0e1ff;
  }
`;

const Login = () => {
  const [studentId, setStudentId] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [errors, setErrors] = useState({
    studentId: '',
    birthdate: ''
  });

  const validateStudentId = (id) => {
    return id.length === 6;
  };

  const validateBirthdate = (date) => {
    return date.length === 8;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {
      studentId: !validateStudentId(studentId) ? '인증코드는 4자리입니다.' : '',
      birthdate: !validateBirthdate(birthdate) ? '인증번호를 전송했습니다.' : ''
    };

    setErrors(newErrors);

    if (!newErrors.studentId && !newErrors.birthdate) {
      // 로그인 로직 구현
      console.log('Login attempt:', { studentId, birthdate });
    }
  };

  return (
    <LoginContainer>
      <Title>로그인</Title>
      <Description>
        학번만 기재할 경우<br />
        메일주소가 자동으로 반영되어서 전송됩니다.
      </Description>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          value={birthdate}
          onChange={(e) => setBirthdate(e.target.value)}
          placeholder="20201014"
        />
        {errors.birthdate && <ErrorMessage>{errors.birthdate}</ErrorMessage>}

        <Input
          type="text"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
          placeholder="123456"
        />
        {errors.studentId && <ErrorMessage>{errors.studentId}</ErrorMessage>}

        <LoginButton type="submit">확인</LoginButton>
      </form>
    </LoginContainer>
  );
};

export default Login;
